function move() {

	/*** Clases clave para ayudar a Bersara ***/
	//                                        //
	// Phaser.Sprite                          //
	// Phaser.Keyboard                        //
	// Phaser.Physics.Arcade                  //
	// Phaser.Physics.Arcade.Body             //
	//                                        //
	/******************************************/

	/* En this.cursors tenemos un objeto con la información de 
	   nuestros 4 controles: flecha arriba (up), flecha abajo (down),
	   flecha derecha (right), flecha izquierda (down)
	   Si queremos saber si alguno de los controles ha sido pulsado
	   podemos consultar su propiedad 'isDown' */
	if (this.cursors.right.isDown) {
		/* En el caso de haber pulsado la flecha derecha nos moveremos 
		   en el sentido positivo del eje X, y por tanto nuestra 
		   velocidad en la X tiene que ser positiva
		   En 'this.walkSpeed' tenemos la velocidad que necesitamos 
		   para caminar */
		this.sprite.body.velocity.x = this.walkSpeed;

		/* Antes de asignar la animación de andar hacia la derecha 
		   tenemos que comprobar que estamos en el suelo. Para ello
		   nos fijaremos en las propiedad 'touching' de body */
		if (this.sprite.body.onFloor() || this.sprite.body.touching.down) {
			/* Para ejecutar una animación usamos la función 'play'
			   de 'sprite'. En este caso ejecutaremos 
			   'player_animation_moveRight' */
			this.sprite.play('player_animation_moveRight', 5, true);
		}

		/* Para cambiar el sentido en el que mira Bersara nos 
		   fijaremos en nuestra dirección. Tenemos dos posibles 
		   direcciones: 'State.LOOKINGLEFT' y 'State.LOOKINGRIGHT' */
		if (this.direction == State.LOOKINGLEFT) {
			/* Solo cambiamos su sentido si estaba mirando en sentido
			   contrario */
			this.direction = State.LOOKINGRIGHT;
		}
	}



	/* Ahora te toca a ti!! Lo primero es comprobar si se ha pulsado
	   la flecha izquierda (this.cursors.left) */


	/* Si es así tenemos que ir en el sentido negativo del eje X, por 
	   lo que asignaremos '-walkSpeed' como nueva velocidad */


	/* Comprobamos si Bersara esta en el suelo, igual que hemos hecho 
	   antes, y si es así cambiamos su animación. Esta vez toca 
	   'player_animations_moveLeft' */


	/* Y por último tenemos que cambiar el sentido de Bersara en caso
	   de que este mirando en sentido contrario */



	/* Si no se ha pulsado nada no moveremos a Bersara. En este caso 
	   lo único que tenemos que hacer es asegurarnos de que se
	   ejecuta la animación correcta, dependiendo de a donde este 
	   mirando Bersara */
	else {
		if (this.direction == State.LOOKINGLEFT) {
			this.sprite.play('player_animation_standUpLeft');
		}
		else if (this.direction == State.LOOKINGRIGHT) {
			this.sprite.play('player_animation_standUpRight');
		}

		/* Y ponemos su velocidad a 0 ya que no tiene que moverse */
		this.sprite.body.velocity.x = 0;
	}
}

function jump() {

	/*** Clases clave para ayudar a Bersara ***/
	//                                        //
	// Phaser.Sprite                          //
	// Phaser.Keyboard                        //
	// Phaser.Physics.Arcade                  //
	// Phaser.Physics.Arcade.Body             //
	//                                        //
	/******************************************/

	/* Para saltar tenemos que comprobar tres cosas:
	   -> Que hayamos pulsado la flecha arriba (this.cursors.up).
	   -> Que estemos tocando el suelo. Esto lo podemos comprobar
	      bien con la funcion 'onFloor' de Body o con la propiedad
	      'touching.down' del sprite.
	   -> Que no estemos ya saltando. Esto lo comprobamos con un
	      timer (this.jumpTimer) */
	if (this.cursors.up.isDown && (this.sprite.body.onFloor() || this.sprite.body.touching.down) && this.game.time.now > this.jumpTimer) {


		/* Al igual que en la función 'move' tenemos que cambiar 
		   la animación, y depende de hacia donde mire Bersara. 
		   Si esta mirando hacia la izquierda ejecutaremos 
		   'player_animation_jumpLeft', y si esta mirando hacia 
		   la derecha 'player_animation_jumpRight' */


		/* Y por supuesto para poder saltar tenemos que cambiar 
		   su velocidad en la Y, ya que tenemos que movernos en el 
		   eje Y. La velocidad de salto esta guardada en 
		   'this.jumpSpeed'*/



		/* Actualizamos el timer del salto. No podremos saltar 
		   hasta dentro de 750 ms */
		this.jumpTimer = this.game.time.now + 750;
	}
}

function snailsMove(enemy) {

	/*** Clases clave para ayudar a Bersara ***/
	//                                        //
	// Phaser.Group                           //
	// Phaser.Physics.Arcade                  //
	// Phaser.Physics.Arcade.Body             //
	//                                        //
	/******************************************/

	/* Cuando el enemigo choca contra algo se da la vuelta 
		y cambia de sentido. Para saber si se ha chocado 
		podemos usar las propiedades 'blocked' o 'touching'
		de Body */
	if (enemy.body.blocked.left || enemy.body.touching.left) {
		enemy.direction = State.LOOKINGLEFT;
	}
		else if (enemy.body.blocked.right || enemy.body.touching.right) {
		enemy.direction = State.LOOKINGLEFT;
	}

	/* Lo siguiente es cambiar su velocidad según la dirección
		a la que este mirando, al igual que hicimos con Bersara.
		También tendremos que cambiar su escala. Con la escala 
		podemos dar la vuelta a nuestros sprites, como si se viesen
		reflejados en un espejo. 
		Para nuestros enemigos sólo tenemos en el spritesheet
		 animaciones para andar hacia la izquierda, así que cambiaremos
		la escala cuando ande hacia la derecha y así no nos harán 
		falta más animaciones.
		Por tanto, si vamos a movernos hacia la izquierda tendremos
		que asignar una velocidad negativa (-enemy.walkSpeed) y una
		escala positiva (enemy.scale = 1) */


	/* Si vamos a movernos hacia la derecha tendremos que asignar una
		velocidad positiva (enemy.walkSpeed) y una escala negativa 
		(enemy.scale = -1) para darle la vuelta a nuestro sprite */



	/* Por último, y no por ello menos importante, tenemos que matar
		a los caracoles si llegan a cierta profundidad. Así que si la 
		'y' es mayor o igual que 980 tendremos que matarles con el 
		metodo 'kill()' */



}

